import React, { useEffect, useState } from 'react'
import { Button, Table, Form } from 'react-bootstrap'
import { fetchAllProject } from '../services/projectService'
import { fetchAllDepartment } from '../services/departmentService'

const ProjectList = () => {
    const [projectList, setProjectList] = useState([])
    const [departmentList, setDepartmentList] = useState([])

    const fetchProjects = async () => {
        try {
            const { data } = await fetchAllProject()
            setProjectList(data)
        } catch (error) {
            console.error(error.message)
        }
    }

    const fetchDepartments = async () => {
        try {
            const { data } = await fetchAllDepartment()
            setDepartmentList(data)
        } catch (error) {
            console.error(error.message)
        }
    }



    useEffect(() => {
        fetchProjects()
        fetchDepartments()
    }, [])

    return (
        <>
            <h2 className='title'>List of Projects</h2>
            <div className='white-board'>
                <div className='d-container'>
                    <span>Departments</span>
                    <Form >
                        {departmentList && departmentList.length > 0 &&
                            departmentList.map((depart) => (

                            <Form.Check
                                type="radio"
                            // label="Male"
                            // name="gender"
                                value={depart.name}
                            // checked={gender === true}
                            // onChange={() => setGender(true)}
                            />
                            ))
                        }



                    </Form>
                </div>
                <div className='table-container'>
                    <Button variant='success'>Create new Project</Button>

                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>Id</th>
                                <th>Project name</th>
                                <th>Description</th>
                                <th>Start date</th>
                                <th>Type</th>
                                <th>Department</th>
                            </tr>
                        </thead>
                        <tbody>
                            {projectList && projectList.length > 0 &&
                                projectList.map((project) => (
                                    <tr key={project.id}>
                                        <td>{project.id}</td>
                                        <td>{project.name}</td>
                                        <td>{project.description}</td>
                                        <td>{project.startDate}</td>
                                        <td>{project.type}</td>
                                        <td>{project.department}</td>
                                    </tr>
                                ))
                            }

                        </tbody>
                    </Table>
                </div>
            </div>

        </>
    )
}

export default ProjectList
