import React, { useEffect, useState } from 'react'
import { Button, Table, Form, FormCheck } from 'react-bootstrap'
import { fetchAllProject, fetchProjectByDepartment } from '../services/projectService'
import { fetchAllDepartment } from '../services/departmentService'
import {Link} from 'react-router-dom'

const ProjectList = () => {
    const [projectList, setProjectList] = useState([])
    const [departmentList, setDepartmentList] = useState([])
    const [first, setfirst] = useState(second)

    const fetchProjects = async () => {
        try {
            const { data } = await fetchAllProject()
            setProjectList(data)
        } catch (error) {
            console.error(error.message)
        }
    }

    const fetchDepartments = async () => {
        try {
            const { data } = await fetchAllDepartment()
            setDepartmentList(data)
        } catch (error) {
            console.error(error.message)
        }
    }

    const handleFilter = async(departId) => {
        setProjectList([])
        try {
            const {data} = await fetchProjectByDepartment(departId)
            
            console.log(projectList)
        } catch (error) {
            console.error(error.message)
        }
    }

    useEffect(() => {
        fetchProjects()
        fetchDepartments()
    }, [])


    return (
        <>
            <h2 className='title'>List of Projects</h2>
            <div className='white-board'>
                <div className='d-container'>
                    <span>Departments</span>
                    <Form >
                            <FormCheck
                                type='radio'
                                label= 'All'
                                name='department'
                            />
                        {departmentList && departmentList.length > 0 &&
                            departmentList.map((depart) => (
                              <div key={depart.id}>  
                            <Form.Check
                                type="radio"
                                label={depart.name}
                                name='department'
                                // id={`default-radio-${depart.id}`}
                                onClick={()=>handleFilter(depart.id)}
                            />
                            </div>
                            ))
                        }



                    </Form>
                </div>
                <div className='table-container'>
                    <Button variant='success'>Create new Project</Button>

                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>Id</th>
                                <th>Project name</th>
                                <th>Description</th>
                                <th>Start date</th>
                                <th>Type</th>
                                <th>Department</th>
                            </tr>
                        </thead>
                        <tbody>
                            {projectList && projectList.length > 0 &&
                                projectList.map((project, index) => (
                                    <tr key={project.id}>
                                        <td>{project.id}</td>
                                        <td>{project.name}</td>
                                        <td>{project.description}</td>
                                        <td>{project.startDate}</td>
                                        <td>{project.type}</td>
                                        <td><Link>{departmentList[index].name}</Link></td>
                                    </tr>
                                ))
                            }

                        </tbody>
                    </Table>
                </div>
            </div>

        </>
    )
}

export default ProjectList
