import React, { useEffect, useState } from 'react'
import { Table } from 'react-bootstrap'
import { Link, useParams } from 'react-router-dom'
import { fetchAllEmployees } from '../services/employeeServices'
import { fetchAllDepartment } from '../services/departmentService'

const EmployeeList = () => {

    const {id} = useParams()
    const [employeeList, setEmployeeList] = useState([])
    const [departmentList, setDepartmentList] = useState([])
    console.log(first)
    var department = departmentList.find((depart)=> depart.id = id)

    const fetchDepartments = async () => {
        try {
            const { data } = await fetchAllDepartment()
            setDepartmentList(data)
        } catch (error) {
            console.error(error.message)
        }
    }

    const filterEmployees = async() => {
        if(!id){
            console.log('No department has been selected!')
            return
        }
        try {
            const {data} = await fetchAllEmployees()
            if(data){
                data.map((e)=>(
                    e.department === id ? setEmployeeList(e) : setEmployeeList()
                ))
            }
        } catch (error) {
            
        }
    }

    useEffect(()=>{
        fetchDepartments()
        filterEmployees()
    },[id])

  return (
    <div>
        <h2>List of Employees</h2>
        <div className='dashboard'>
            <Link to={'/'}>Home page</Link>
            
            <div className='title'>Department: {department ? department.name : ""}</div>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Employee name</th>
                        <th>Date of birth</th>
                        <th>Gender</th>
                        <th>Position</th>
                    </tr>
                </thead>
                <tbody>

                </tbody>
            </Table>
        </div>
    </div>
  )
}

export default EmployeeList
